import React, { useEffect, useRef, useState } from "react";
import { gql, useQuery } from "@apollo/client";

import Accordion from "react-bootstrap/Accordion";
import Form from 'react-bootstrap/Form';

import TableView from "./tableView";
const LIST_CONTINENTS = gql`
  query {
    continents {
      name
      countries {
        name
        languages {
          native
          name
          code
        }
        phones
        emoji
        capital
        currency
      }
    }
  }
`;

export default function ContinentList() {
  const { data, loading, error } = useQuery(LIST_CONTINENTS);
  const [selectContinent, setSelectContinent] = useState('Select Continent')

  if (loading || error) {
    return <p>{error ? error.message : "Loading..."}</p>;
  }


  const handleContinentSelect=(continent)=>{
    console.log(continent)
    setSelectContinent(continent)
  }
  return (
    <>

  <Form.Select>
      <option>Open this select menu</option>
      <option value="1">One</option>
      <option value="2">Two</option>
      <option value="3">Three</option>
    </Form.Select>
    <select onChange={(e)=>handleContinentSelect(e)}>
        {data.continents.map((continent, index) => {
          return (
            <option key={index} value={continent.name}>{continent.name}</option>
          )})}
    </select>
    

      <Accordion defaultActiveKey="0">
        {data.continents.map((continent, index) => {
          return (
            <Accordion.Item eventKey={index}>
              <Accordion.Header>{continent.name}</Accordion.Header>
              <Accordion.Body>
                <TableView tableData={continent.countries} />
              </Accordion.Body>
            </Accordion.Item>
          );
        })}
      </Accordion>
    </>
  );
}
