import React, { useEffect, useRef, useState } from "react";
import { gql, useQuery } from "@apollo/client";
import Table from "react-bootstrap/Table";
import Accordion from "react-bootstrap/Accordion";

const LIST_CONTINENTS = gql`
  query {
    continents {
      name
      countries {
        name
        languages {
          native
          name
          code
        }
        phones
        emoji
        capital
        currency
      }
    }
  }
`;

export default function ContinentList() {
  const { data, loading, error } = useQuery(LIST_CONTINENTS);

  if (loading || error) {
    return <p>{error ? error.message : "Loading..."}</p>;
  }

  // const [currentPage, setCurrentPage] = useState(1)
  // const perPage = 5;
  // const lastIndex = currentPage * perPage
  // const firstIndex = lastIndex - perPage
  // const records = slice(firstIndex, lastIndex)
  
  return (
    <>
      <Accordion defaultActiveKey="0">
        {data.continents.map((continent, index) => {
          return (
            <Accordion.Item eventKey={index}>
              <Accordion.Header>{continent.name}</Accordion.Header>
              <Accordion.Body>
                <Table striped bordered hover>
                  <thead>
                    <tr>
                      <td>Countries</td>
                      <td>Emoji</td>
                      <td>Capital</td>
                      <td>Currency</td>
                    </tr>
                  </thead>
                  <tbody>
                    {continent.countries.map((data) => {
                      return (
                        <tr>
                          <td>{data.name}</td>
                          <td>{data.emoji}</td>
                          <td>{data.capital}</td>
                          <td>{data.currency}</td>
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
              </Accordion.Body>
            </Accordion.Item>
          );
        })}
      </Accordion>
    </>
  );
}
