import Table from "react-bootstrap/Table";
import React,{useState} from "react";
import Pagination from 'react-bootstrap/Pagination';

 const TableView=({tableData})=>{

    

  const [currentPage, setCurrentPage] = useState(1)
  const perPage = 5;
  const lastIndex = currentPage * perPage
  const firstIndex = lastIndex - perPage
  const records = tableData.slice(firstIndex, lastIndex)
  const newPage = Math.ceil(tableData.length / perPage)
  const numbers = [...Array(newPage + 1 ).keys()].slice(1)



  const prevPage =()=>{
    if(currentPage > 1){
        setCurrentPage(currentPage - 1)
    }
  }
  const NextPage =()=>{
    if(currentPage < newPage){
        setCurrentPage(currentPage + 1)
    }
  }
    return (
        <>
            <div>
                <form className="col-md-8">
                    <div className="row">
                        <div className="col-md-4 form-group">
                            <label className="form-label">Search</label>
                            <input type="text" className="form-control"/>
                        </div>
                        <div className="col-md-4">
                            <label className="form-label">Sort</label>
                            <select className="form-select">
                                <option>
                                    Choose Option
                                </option>
                                <option>
                                    Asc
                                </option>
                                <option>
                                    Des
                                </option>
                            </select>
                        </div>
                    </div>
                </form>
            </div>
            <Table striped bordered hover>
                <thead>
                <tr>
                    <td>Countries</td>
                    <td>Emoji</td>
                    <td>Capital</td>
                    <td>Currency</td>
                </tr>
                </thead>
                <tbody>
                {records.map((data) => {
                    return (
                    <tr>
                        <td>{data.name}</td>
                        <td>{data.emoji}</td>
                        <td>{data.capital}</td>
                        <td>{data.currency}</td>
                    </tr>
                    );
                })}
                </tbody>
            </Table>
            <Pagination>
                <Pagination.Prev onClick={prevPage}/>
                {numbers.map(num=>{
                        return  <Pagination.Item onClick={()=> setCurrentPage(num)} className={currentPage === num ? "active" : ''}>{num}</Pagination.Item>
                    })}
                <Pagination.Next onClick={NextPage}/>
            </Pagination>
        </>
        )
 }


 export default TableView
