import Table from "react-bootstrap/Table";
import React,{useState} from "react";
import Pagination from 'react-bootstrap/Pagination';

 const TableView=({tableData})=>{


    const [search, setSearch] = useState()
        
    const [currentPage, setCurrentPage] = useState(1)
   
    const [sorted, setSorted] = useState(tableData)
    
    // SEARCH METHOD
    const handleSearch = (e)=>{
        setSearch(e.target.value)
        const filterSearch = records.filter((index,val)=>{
          return records.indexOf(val) == index 
      })
      console.log(records, 'filterSearch' )
    }



    //SORTING METHOD
    const tableHeadrer = [
        Countries,
        Emoji,
        Capital,
        Currency
    ]
    const handleSortingMethod=()=>{
        console.log("")
    }


    
    // PAGINATION CODE

    const perPage = 5;
    const lastIndex = currentPage * perPage
    const firstIndex = lastIndex - perPage
    const records = tableData.slice(firstIndex, lastIndex)
    const newPage = Math.ceil(tableData.length / perPage)
    const numbers = [...Array(newPage + 1 ).keys()].slice(1)

    const prevPage =()=>{
        if(currentPage > 1){
            setCurrentPage(currentPage - 1)
        }
    }
    const NextPage =()=>{
        if(currentPage < newPage){
            setCurrentPage(currentPage + 1)
        }
    }


    return (
        <>
            <div>
                <form className="col-md-10">
                    <div className="row">
                        <div className="col-md-6 d-flex align-items-center">
                            <label className="form-label me-2 fw-bold">Search</label>
                            <input type="text" className="form-control" value={search} name='search' onChange={handleSearch} />
                        </div>
                    </div>
                </form>
            </div>
            <Table striped bordered hover>
                <thead>
                <tr onClick={handleSortingMethod}>
                    {tableHeadrer.map(thData=>{
                        return  <th>{thData}{currentPage ? ' ðŸ”½'
                        : ' ðŸ”¼'}</th>
                    })}
                   
                    {/* <th>Emoji</th>
                    <th>Capital</th>
                    <th>Currency</th> */}
                </tr>
                </thead>
                <tbody>
                {records.map((data) => {
                    return (
                    <tr>
                        <td>{data.name}</td>
                        <td>{data.emoji}</td>
                        <td>{data.capital}</td>
                        <td>{data.currency}</td>
                    </tr>
                    );
                })}
                </tbody>
            </Table>
            <Pagination>
                <Pagination.Prev onClick={prevPage}/>
                {numbers.map(num=>{
                        return  <Pagination.Item onClick={()=> setCurrentPage(num)} className={currentPage === num ? "active" : ''}>{num}</Pagination.Item>
                    })}
                <Pagination.Next onClick={NextPage}/>
            </Pagination>
        </>
        )
 }


 export default TableView
