import Table from "react-bootstrap/Table";
import React,{useState} from "react";
import Pagination from 'react-bootstrap/Pagination';

 const TableView=({tableData})=>{

    

  const [currentPage, setCurrentPage] = useState(1)
  const perPage = 5;
  const lastIndex = currentPage * perPage
  const firstIndex = lastIndex - perPage
  const records = tableData.slice(firstIndex, lastIndex)
  const newPage = Math.ceil(tableData.length / perPage)
  const numbers = [...Array(newPage + 1 ).keys()].slice(1)

  const prevPage =()=>{
    if(currentPage < records){
        setCurrentPage(currentPage - 1)
    }
  }
  const NextPage =()=>{
    if(currentPage < records - 1){
        setCurrentPage(currentPage + 1)

    }
    console.log(currentPage, tableData.length - 1)

  }
    return (
        <>

            <Table striped bordered hover>
                <thead>
                <tr>
                    <td>Countries</td>
                    <td>Emoji</td>
                    <td>Capital</td>
                    <td>Currency</td>
                </tr>
                </thead>
                <tbody>
                {records.map((data) => {
                    return (
                    <tr>
                        <td>{data.name}</td>
                        <td>{data.emoji}</td>
                        <td>{data.capital}</td>
                        <td>{data.currency}</td>
                    </tr>
                    );
                })}
                </tbody>
            </Table>
            <Pagination>
                <Pagination.First  onClick={()=> setCurrentPage(firstIndex)}/>
                <Pagination.Prev onClick={prevPage}/>
                    {numbers.map(num=>{
                        return  <Pagination.Item onClick={()=> setCurrentPage(num)} className={currentPage === num ? "active" : ''}>{num}</Pagination.Item>
                    })}
                <Pagination.Next onClick={NextPage}/>
                <Pagination.Last onClick={()=> setCurrentPage(lastIndex)}/>
            </Pagination>
        </>
        )
 }


 export default TableView
