import Table from "react-bootstrap/Table";
import React,{useState} from "react";
import Pagination from 'react-bootstrap/Pagination';

 const TableView=({tableData})=>{

    

  const [currentPage, setCurrentPage] = useState(1)
  const perPage = 5;
  const lastIndex = currentPage * perPage
  const firstIndex = lastIndex - perPage
  const records = tableData.slice(firstIndex, lastIndex)
  const newPage = Math.ceil(tableData.length / perPage)
  const numbers = [...Array(newPage + 1 ).keys()].slice(1)

  console.log(numbers)
    return (
        <>

            <Table striped bordered hover>
                <thead>
                <tr>
                    <td>Countries</td>
                    <td>Emoji</td>
                    <td>Capital</td>
                    <td>Currency</td>
                </tr>
                </thead>
                <tbody>
                {tableData.slice(currentPage * 10 - 10, currentPage * 10).map((data) => {
                    return (
                    <tr>
                        <td>{data.name}</td>
                        <td>{data.emoji}</td>
                        <td>{data.capital}</td>
                        <td>{data.currency}</td>
                    </tr>
                    );
                })}
                </tbody>
            </Table>
            <Pagination>
                <Pagination.First />
                <Pagination.Prev onClick={perPage}/>
                <Pagination.Item>{1}</Pagination.Item>
                <Pagination.Ellipsis />

                <Pagination.Item>{10}</Pagination.Item>
                <Pagination.Item>{11}</Pagination.Item>
                <Pagination.Item active>{12}</Pagination.Item>
                <Pagination.Item>{13}</Pagination.Item>
                <Pagination.Item disabled>{14}</Pagination.Item>

                <Pagination.Ellipsis />
                <Pagination.Item>{20}</Pagination.Item>
                <Pagination.Next />
                <Pagination.Last />
            </Pagination>
        </>
        )
 }


 export default TableView
